class UpdateControl extends Component{
  constructor(props){
    super(props);
      let _contents = Array.from(this.props.data);
      let i=0;
      while(i < _contents.length){
        if(_contents[i].id === (this.props.select*1)){
          break;
        }
        i++;
      }
    this.state = {
      id:this.props.data[i].id,
      title:this.props.data[i].title,
      desc:this.props.data[i].desc
    }
  }
  ControlChange = (e) => {
    e.preventDefault();
    this.setState({[e.target.name]:e.target.value}) //[]
  }
  ControlUpdate = (e) => {
    e.preventDefault();
    this.props.onUpdate(e.target.id.value, e.target.title.value, e.target.desc.value);
  }
  render(){
    // console.log('update contents')
    return(
      <form action="/update" onSubmit={this.ControlUpdate}>
        <div>Update</div>
        <input type="hidden" name='id' value={this.state.id}/>
        <input type="text" name='title' value = {this.state.title} onChange={this.ControlChange}/>
        <input type="text" name='desc' value = {this.state.desc} onChange={this.ControlChange}/>
        <input type="submit"/>
      </form>
    )
  }
}

class DeleteControl extends Component{
  ControlDelete = (e) => {
    e.preventDefault();
    this.props.onDelete();
  }
  render(){
    // console.log('delete contents')
    return(
      <form action="/delete" onSubmit={this.ControlDelete}>
        <div>Delete</div>
        <input type="submit"/>
      </form>
    )
  }
}

class CreateControl extends Component{
  // shouldComponentUpdate(newProps, newState){
  //   console.log(this.props.data, newProps.data)
  // }
  ControlCreate = (e) => {
    e.preventDefault();
    this.props.onCreate(e.target.title.value, e.target.desc.value);
    e.target.title.value =''
    e.target.desc.value = ''
  }
  render(){
    // console.log('create contents')
    return(
      <form action="/create" onSubmit={this.ControlCreate}>
        <div>Create</div>
        <input type="text" name='title' placeholder='Title'/>
        <input type="text" name='desc' placeholder='Description'/>
        <input type="submit"/>
      </form>
    )
  }
}

{/* 리스트 뿌려주기 */}
class Contents extends Component{
  RadioClick = (e) => {
    this.props.onSelectContent(e.target.value);
  }
  RadioChange = (e) => {
    this.setState({selectcontent:e.target.value})
  }
  render(){
    // console.log('contents render')
    let lists = [];
    let data = this.props.data
    let i = 0;
    while(i<data.length){
      lists.push(<input type="radio" name="group1" value={data[i].id} 
        onClick={this.RadioClick} onChange={this.RadioChange}/>, <span>{data[i].title}</span>);
        //<li key={data[i].id}>{data[i].title} {data[i].desc}</li>);
      i = i+1
    }
    return(
      <div>
        <p>{lists}</p>
      </div>
    )
  }
}

class User extends Component{
  SubjectClick = (e) => {
    e.preventDefault();
    this.props.onChangeUser();
  }
  render(){
    // console.log('user render')
    return(
      <div>
        <p>user : {this.props.user}</p>
        <input type="button" value="Login" onClick={this.SubjectClick}/>
      </div>
    )
  }
}


class Immutable extends Component{
  render(){
    let a = [1, 2]; //배열 복제
    let b = Array.from(a);
    console.log(a, b, a===b)
    b.push(3);
    console.log(a,b)

    let aa = {name:'Chulsoo'}; //개체 복제
    let bb = Object.assign({},aa);
    console.log(aa, bb, aa===bb)
    bb.name = 'yeonghee'
    console.log(aa, bb, aa===bb)

    return(
      <div>
        Immutable Test
      </div>
    )
  }
}

